entranceT = [42,4]

def nearestExit(maze, entrance):
    maxY, maxX = len(maze)-1, len(maze[0])-1 #get borders
    maze[entrance[0]][entrance[1]] = "+" #entrance cant be exit now
    posList = [entrance]
    #get all unwalked and free positions directly around a coordinate
    def around():
        res = []
        for pos in posList:
            y,x = pos  
            for posN in [[y-1,x],[y,x-1],[y,x+1],[y+1,x]]: #Check Up, Left, Right, Down
                if 0<=posN[0]<=maxY and 0<=posN[1]<=maxX : #is pos inside the maze?
                    if maze[posN[0]][posN[1]] == ".": #is the spot unwalked yet and free?
                        res.append(posN) #return this pos
        return res #return the updated posList
    posList = around() #get the first free positions around
    steps = 1 #first distance
    print(steps)
    while posList != []: #empty if all places walked
        print(steps)
        for pos in posList:
            if (0 in pos) or maxY == pos[0] or maxX == pos[1]: 
                return steps #reached exit!
            maze[pos[0]][pos[1]] = "+" #mark as walked
        steps += 1 #used the posList, need to walk further!
        posList = around() #get free positions around
    return -1 #unreachable!
nearestExit(mazeT, entranceT)